import React from 'react';
import style from '../styles/home.css'
export default class Home extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            load:false
        }
    }
    componentDidMount() {
    window.show()
    }
    render() {
        return (
            <div>
                <h2>React</h2>
                <p className={style.fontsize}>首页</p>
                <button onClick={this.show}>load</button>
            </div>
        )
    }
    show(){
        const load=this.state.load
        this.setState({
            load:!load
        })
    }
}
